import { AfterViewChecked, Component, ElementRef, EventEmitter, Input, OnChanges, OnInit, Output, SimpleChanges, ViewChild } from '@angular/core';
import { Client } from '@stomp/stompjs';
import { Message } from 'src/app/models/Message';
import { ChatService } from 'src/app/services/chat.service';
import { WsService } from 'src/app/services/ws.service';

@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css']
})
export class ChatComponent implements OnInit, AfterViewChecked, OnChanges {

  private connected: boolean = false;
  public messages: Message[] = [];
  public message: Message = new Message();
  @Input() activeContact: any | undefined
  @Input() isDarkMode = false
  @Output() sendMessage2 = new EventEmitter<string>()

  @ViewChild("messagesContainer") private messagesContainer!: ElementRef

  newMessage = ""
  isTyping = false

  constructor(private services_ws: WsService, private ctS: ChatService) { }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['activeContact'] && this.activeContact) {
      console.log(this.activeContact)
      this.ctS.getMessages(this.activeContact.id).subscribe({
        next: (data) => {
          console.log(data)
          this.messages = data
        }
      })
    }
  }


  ngOnInit(): void {
    this.services_ws.initializeWebSocketConnection()
  }

  ngAfterViewChecked() {
    this.scrollToBottom()
  }

  scrollToBottom(): void {
    try {
      this.messagesContainer.nativeElement.scrollTop = this.messagesContainer.nativeElement.scrollHeight
    } catch (err) { }
  }

  getStatusText(): string {
    if (!this.activeContact) return ""

    if (this.activeContact.status === "online") {
      return "En línea"
    } else if (this.activeContact.status === "typing") {
      return "Escribiendo..."
    } else {
      return "Última vez hoy a las " + this.activeContact.lastSeen
    }
  }

  getStatusIcon(status: string): string {
    switch (status) {
      case "sent":
        return "check"
      case "delivered":
        return "done_all"
      case "read":
        return "done_all"
      default:
        return "schedule"
    }
  }

  formatTime(date: Date): string {
    return date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })
  }

  onSendMessage(): void {
    if (this.newMessage.trim()) {
      this.newMessage = ""
      setTimeout(() => {
        this.isTyping = true
        setTimeout(() => {
          this.isTyping = false
        }, 2000)
      }, 1000)
    }
  }

  sendMessage(): void {
    this.services_ws.sendMessage(this.message)
  }

  trackByMessageId(index: number, message: any): string {
    return message.id; // o message.id si ese es el campo único
  }


  ngOnDestroy(): void {
    this.services_ws.disconnect()
  }

}
