import { Component, OnInit } from '@angular/core';
import { AuthService } from './services/auth.service';
import { ThemeService } from './services/theme.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  isDarkMode = false
  activeContactId = 0;
  authenticated: boolean = false

  constructor(private auth: AuthService, private theme: ThemeService) { }

  ngOnInit(): void {
    this.auth.authenticated$.subscribe(auth => {
      this.authenticated = auth;
    });
    this.isDarkMode = this.theme.isDarkMode;
  }

  set setActiveContact(contactId: any) {
    this.auth.setActiveContact(contactId);
    // this.activeContactId = contactId
  }

  get getActiveContact(){
    return this.activeContactId
  }

  toggleTheme() {
    this.theme.toggleTheme();
    this.isDarkMode = this.theme.isDarkMode;
  }

}
